INSERT INTO `dede_addonarticle` VALUES (11, 21, '<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-family: 宋体; font-size: 12pt; ">美国<strong><a href="http://www.bjqingyang.com/yundongfu/">运动服</a>品牌</strong>Columbia推出一项号称为全方位冻结(Omni-Freeze&nbsp;Zero)的活化汗水凉感新技术，该公司指称，穿着Omni-Freeze&nbsp;Zero布料衣服的人的汗水在接触到在这种衣料上植入的无数的蓝色小环(littlebluerings)时会发生反应，瞬间产生持续的凉感。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; ">美国运动服品牌<strong>Columbia</strong>推出</span><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; ">的</span><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; ">这种凉感材料可做成40多种的衣服，从机能性衬衫到运动鞋靴以及从棒球帽到贴身短裤等，也包括男女服饰品及鞋靴类产品，同时这种布料也有用在生产Columbia最成功的机能性钓鱼装系列产品(Performance&nbsp;Fishing&nbsp;Gear;PFG)。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; ">Columbia的全球行销副总DanHanson表示，我们相信消费者将会发现他们辛苦流出的汗水除了被蒸发之外还有其他的用途;每个人都知道在冬天穿Columbia的产品很保暖，现在他们更可信赖在夏天穿业界开发出来的这种凉感材质的Columbia的产品会很舒适，全方位冻结(Omni-Freeze&nbsp;Zero)的活化汗水凉感材料是最新的创新性技术，将可使Columbia转化成一个四季可穿的可信赖的衣服品牌。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: center; ">\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; ">定做运动服,运动服制作订做,运动服加工厂,运动服生产厂家-淘宝网运动服供应商首选庆洋！&nbsp;</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 宋体; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n                            </div>\r\n                        </div>', '', '', '');
INSERT INTO `dede_addonarticle` VALUES (12, 21, '<p>&nbsp;</p>\r\n<p><span style="font-family: 微软雅黑; font-size: 12pt; text-align: justify; ">最近，在热传一种能抗暑热的&ldquo;日光衬衫&rdquo;，此衬衫是有印度加尔各答GonChaudhury研究，他阐述了一种新的理念&ldquo;日光衬衫&rdquo;，该<a href="http://www.bjqingyang.com/chenshan/">衬衫</a>内置太阳能电池和风扇。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; ">GonChaudhury先生说，他们正开发一种技术，将一块小太阳能电池镶嵌到衬衫纤维或者其口袋中，印度时报报道。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; ">太阳能电池的尺寸在2.5-3英寸之间，嵌入到每个衬衫当中，产生大约400瓦的能源，足以给手机、平板电脑和其他数字设备充电。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; ">关于这个理念的细节，GonChaudhury先生说，这种衬衫有两层，其中一层有2-4个小风扇，利用太阳能电力工作。这些风扇比计算机中的风扇小。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; ">GonChaudhury先生说，人们不需要一直穿这种衬衫，因为这种衬衫暴露在阳光下一定时间就能够为所有特定的数字设备充电。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; background-position: initial initial; background-repeat: initial initial; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; 