INSERT INTO `dede_addonarticle` VALUES (15, 21, '<p>&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">在上海一家普拉达（PRADA）专卖店，销售人员介绍，买动辄上万元的奢侈皮具，开发票可以是&ldquo;礼品&rdquo;，也可以是&ldquo;<a href="http://www.bjqingyang.com/">工作服</a>&rdquo;，显然为了报销方便。一位奢侈品品牌销售顾问甚至表示，不管购买的是什么商品，都可以开具&ldquo;办公用品&rdquo;发票，且不会出现商品明细，方便入账。这位销售顾问说，不少客户购买奢侈品&ldquo;不问价格&rdquo;，&ldquo;因为付钱的都不是他们自己&rdquo;。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">中国社会科学院财经战略研究院研究员申恩威说，一些单位之间&ldquo;公对公&rdquo;送礼可谓巧立名目，购买礼品的资金通常笼统称为&ldquo;办公用品&rdquo;&ldquo;食品&rdquo;等，至于购买产品的用途和去向等具体信息不会注明。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">对此，同济大学法学院副教授刘春彦说，报销发票不允许开&ldquo;礼品&rdquo;等项目，必须标注具体的商品信息，便于对商品去向进行监督，严查企业主向公务人员送礼行为。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n                            </div>\r\n                        </div>', '', '', '');
INSERT INTO `dede_addonarticle` VALUES (16, 21, '<p>&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">在韩国、日本，校服大都采用制服式，而我们中小学校服采用的大都是运动装式校服，针对制服式校服和运动装式校服选择展开激烈争辩。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">北京服装学院校服研发中心主任曹永看来，由于普遍的设计缺陷，目前大多数中小学选购的运动装式校服为&ldquo;箱式&rdquo;校服，缺乏符合人体运动工程学的剪裁设计和面料选择。并且家长往往为学生选购尺码偏大的校服，希望能穿更长时间，导致学生长时间穿着肥大的校服，反而不利于运动。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">制服式校服体现了更强的仪式感，在开学典礼、成人仪式、毕业证发放仪式上穿着制服式校服，是对学生进行礼仪教育的有效手段。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">针对校服如何选择一事，你是什么看法，欢迎踊跃参与评论。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n                            </div>\r\n                        </div>', '', '', '');
INSERT INTO `dede_addonarticle` VALUES (17, 21, '<p>&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">前段时间，泉水节首批300件<a href="http://www.bjqingyang.com/">文化衫</a>已经设计制作到位，但泉水节开幕式演出及花船活动文化衫款式尚未确定，现泉水节办公室面向社会</span><span style="font-size: 12pt; font-family: 微软雅黑; ">征集花船装饰</span><span style="font-size: 12pt; font-family: 微软雅黑; ">设计及<a href="http://www.bjqingyang.com/wenhuashan/">文化衫设计制作</a>方案。文化衫设计及文化衫制作要求如下：</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">文化衫设计、</span><span style="font-size: 12pt; font-family: 微软雅黑; ">文化衫</span><span style="font-size: 12pt; font-family: 微软雅黑; ">制作需符合以下要求：前胸右侧上方位置，应按照规定的标准尺寸印制或者缝制泉水节标识。企业赞助支持或者企业专用的文化衫，则可以在其他位置印制或者缝制其企业标识。同时，经泉水节办公室推荐或者同意，可在文化衫背部印制有关图案或者文字口号等。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: center; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n                            </div>\r\n                        </div>', '', '', '');
INSERT INTO `dede_addonarticle` VALUES (18, 21, '<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">据悉，首届上海国际职<a href="http://www.bjqingyang.com/">业服装</a>博览会将于2013年6月5日至7日在上海光大会展中心闪亮登场。上海国际职业服装博览会将秉承&ldquo;塑造行业风标&bull;构筑世界品牌&rdquo;的办展宗旨，紧紧围绕&ldquo;职业服装&rdquo;的主题，拓展团购及定制等新的营销渠道，促进职业服装品牌建设，推动职业服装行业发展。展会得到了国内外众多知名品牌的积极响应，反响热烈，中国、日本、韩国、意大利、英国等国内外的知名服装品牌踊跃报名参展，目前已有波司登、苹果、东盟集团、奥斯迪、梅兰、津海集团、高的雅以及帝人等企业纷纷入驻，湖南、江苏、河南等省、市服装协会、商会也纷纷组团参展，25000平方米的展览面积已几近预订一半。日本《纤维NEWS》在得知展会主办的第一时间就与展会主办接洽协议合作，他们将携日本知名<a href="http://www.bjqingyang.com/pinpai/zhiyezhuang/">职业服装品牌</a>组团参加本次展会。在宏观经济出现秋寒的时期，职业服装博览会的火爆远超主办方的预期，展示面积甚至出现了翻倍扩张的现象。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">上海国际职业服装博览会，是目前我国职业服装领域唯一一个集品牌塑造、形象展示、新品发布、产品推介、渠道建设和行业信息交流以及商贸合作的专业性国际展会，为中国服装企业走向世界、国外品牌进入中国市场搭建了一个现代化的宣传窗口和交易交流平台。展会形成了完善的职业装产业链，尤其注重专业观众的选择与推介，为政府机构、银行、证券、保险、学校、通信、机械、矿山、石化、建筑、烟草、地产、餐饮酒店等行业搭建了一个崭新的现代化商贸交流平台。这种领先国际的超级会展服务模式，使参展商的信心和采购商的满意度大大增加。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; ">本届展会有几大亮点，第一是专业化：本届职业服装展定位于专业展会、谢绝零售，通过&ldquo;三主一辅&rdquo;，打造专业职业服装博览会：一是以中高端品牌的代理加盟招商与贸易洽谈、品牌展示、与百货商厦的对接为主;二是对接国际国内设计师与品牌企业为主;三是以帮助国际品牌开拓中国市场为主;四是以国际采购商借此平台采购为辅。第二是国际化：本届职业服装展特邀国外专业媒体、政府、商务网络以及各国驻华代表处、各省市商会协会等汇聚一堂，致力将&ldquo;上海国际职业服装博览会&rdquo;打造成为行业发展和流行趋势的风向标，是国际品牌进入中国市场、中国企业走向国际市场的桥头堡。</span></p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; ">&nbsp;</p>\r\n<p class="p0" style="margin-bottom: 0pt; margin-top: 0pt; text-align: justify; background-position: initial initial; background-repeat: initial initial; "><span style="font-size: 12pt; font-family: 微软雅黑; "><o:p></o:p></span></p>\r\n                            </div>\r\n                        </div>', '', '', '');
INSERT INTO `dede_addonarticle` VALUES (19, 21, '<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<h1 style="text-align: center"><span style="font-size: large"><span style="font-family: ''宋体''; color: rgb(0,0,0); mso-spacerun: ''yes''">凡客与优衣库上演价格大战</span></span></h1>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''"><o:p></o:p></span></p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">VANCL凡客诚品，互联网快时尚品牌。高性价比的自有品牌，全球时尚的无限选择，最好的用户体验。</span><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">优衣库属于日本品牌，在钓鱼岛事件的争执下，或多或少的对优衣库在中国市场有所影响，对此，优衣库采取了价格大幅下调策略，</span><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">凡客诚品26日下午宣布</span><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">，</span><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">即日起至10月7日，凡客将部分法兰绒衬衫的价格由129元、99元降至68元一件，以应对此前将价格大幅下调的优衣库。</span></p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<div style="text-align: center"><a target="_blank" href="http://www.bjqingyang.com/xiuxian-chenshan/"><img style="cursor: pointer" onclick="window.open(''/uploads/allimg/120929/1_120929095621_1.jpg'')" border="0" alt="凡客与优衣库上演价格大战" src="/uploads/allimg/c130506/13CS1531P160-12310.jpg" width="583" height="537" /></a><br />\r\n&nbsp;</div>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''"><o:p></o:p></span></p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">今秋继续成主角的法兰绒格子<span style="color: #ff0000"><strong><a href="http://www.bjqingyang.com/chenshan/"><span style="color: #ff0000">衬衫</span></a></strong></span>，正沦为各大快时尚品牌占据市场的&ldquo;特卖品&rdquo;。凡客诚品26日下午宣布，即日起至10月7日，凡客将部分法兰绒衬衫的价格由129元、99元降至68元一件，以应对此前将价格大幅下调的优衣库。</span></p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''"><o:p></o:p></span></p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: ''yes''">对于此次降价，凡客CEO陈年在微博上称&ldquo;法兰绒需要大动作&rdquo;，此次降价的产品占了全线产品过半比例，可见其大手笔的力度。</span></p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0">&nbsp;</p>\r\n<p style="text-align: justify; margin-top: 0pt; margin-bottom: 0pt; background: rgb(255,255,255)" class="p0"><span style="font-family: ''宋体''; color: rgb(0,0,0); font-size: 10.5pt; mso-spacerun: '